mapper.select:
SELECT *
FROM MEMBER
WHERE MEM_STATUS = 'Y' AND MEM_PWD != 'kakao'

	AND MEM_ID = #{memId}


	AND MEM_NAME = #{memName}


	AND MEM_PHONE = #{memPhone} 


	AND MEM_EMAIL = #{memEmail}

mapper.update:
UPDATE MEMBER
SET MEM_PWD = #{memPwd} 
WHERE MEM_ID = #{memId}

UPDATE ADDRESS
SET ADD_DEFAULT = #{addDefault}
WHERE MEM_ID = #{memId}

UPDATE ADDRESS
SET ADD_NAME = #{addName}, ADD_RECIPIENT = #{addRecipient}, ADD_ADDRESS = #{addAddress}, ADD_PHONE = #{addPhone}, ADD_DEFAULT = #{addDefault}
WHERE ADD_NO = #{addNo}

UPDATE MEMBER
SET MEM_STATUS = 'N'
WHERE MEM_ID = #{memId}

mapper.delete:
UPDATE ADDRESS
SET ADD_STATUS = 'N'
WHERE ADD_NO = #{addNo}

mapper.update:
UPDATE MEMBER

	SET MEM_NICKNAME = #{memNickName}


	SET MEM_NAME = #{memName}


	SET MEM_EMAIL = #{memEmail} 


	SET MEM_PHONE = #{memPhone}


	SET MEM_ADDRESS = #{memAddress}

WHERE MEM_ID = #{memId}

UPDATE MEMBER
SET MEM_FILENAME = #{memFileName}
WHERE MEM_ID = #{memId}

UPDATE MEMBER
SET MEM_FILENAME = 'member/images/defaultProfile.svg'
WHERE MEM_ID = #{memId}

mapper.select:
 
		SELECT COUNT(*)
		FROM MEMBER
		WHERE 1=1
		
			AND MEM_ISADMIN = #{isAdmin}
		
		
			AND ${keyword} like '%'|| #{searchText} ||'%'
		
		
			AND MEM_DATE  >=  #{startDate}
		
		
			AND MEM_DATE  <=  #{endDate}
		
		
			AND MEM_DATE BETWEEN #{startDate} AND #{endDate}
		
		
			AND MEM_STATUS = #{status}

SELECT *
FROM MEMBER
WHERE 1=1

	AND MEM_ISADMIN = #{isAdmin}


	AND ${keyword} like '%'|| #{searchText} ||'%'


	AND MEM_DATE  >=  #{startDate}


	AND MEM_DATE  <=  #{endDate}


	AND MEM_DATE BETWEEN #{startDate} AND #{endDate}


	AND MEM_STATUS = #{status}

ORDER BY MEM_STATUS DESC, MEM_DATE DESC

mapper.update:
UPDATE MEMBER
SET MEM_ISADMIN = #{memIsAdmin}
WHERE MEM_ID = #{memId}

UPDATE MEMBER
SET MEM_NICKNAME = #{memNickName}, MEM_EMAIL = #{memEmail}, MEM_PHONE = #{memPhone}, MEM_ADDRESS = #{memAddress}
WHERE MEM_ID = #{memId}

mapper.select:
SELECT COUNT(*)
FROM INQUIRY
WHERE MEM_ID = #{id}

	AND INQ_DATE  >=  #{startDate}


	AND INQ_DATE  <=  #{endDate}


	AND INQ_DATE BETWEEN #{startDate} AND #{endDate}

SELECT *
FROM INQUIRY 
WHERE MEM_ID = #{id}

	AND INQ_DATE  >=  #{startDate}


	AND INQ_DATE  <=  #{endDate}


	AND INQ_DATE BETWEEN #{startDate} AND #{endDate}

ORDER BY INQ_NO DESC

SELECT INQ_FILENAME
FROM INQUIRY
WHERE INQ_NO = #{inqNo}
